mapper.select:
select count(1) from merchant
where user_name = #{userName}

  AND id != #{id}

select count(1) from merchant
where merchant_num = #{merchantNum}

  AND id != #{id}

select count(1) from merchant
where merchant_name = #{merchantName}

  AND id != #{id}

mapper.update:
update merchant
set deleted_flag = #{deletedFlag}
where id = #{id}
